#!/bin/bash

rm -rf tmp/ doc/generated-doc
mkdir -p tmp/
cp -rf dist tmp/

# build typedoc:
./node_modules/.bin/typedoc --name 'Mendix Model SDK' -m commonjs -t es5 --out doc/generated-doc --includeDeclarations -mode file --theme doc/custom_typedoc_theme --readme 'none' tmp/doc-index.ts --excludeExternals --externalPattern tmp/**/* --ignoreCompilerErrors

# Note: typedoc installs its own TypeScript compiler, and in version 1.6.2.
# This avoids certain problems with typedoc not being updated to 1.8.x yet.

